import React, { useState } from 'react';
import Button from './components/ui/button';
import Input from './components/ui/input';
import Progress from './components/ui/progress';
import { ThemeProvider } from './context/ThemeContext'; // Contexto de tema
import './App.css';

const App = () => {
  const [file, setFile] = useState(null);
  const [fileType, setFileType] = useState('');
  const [convertedFile, setConvertedFile] = useState(null);
  const [progress, setProgress] = useState(0);
  const [error, setError] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const apiURL = process.env.REACT_APP_API_URL;

  const handleFileUpload = (event) => {
    const uploadedFile = event.target.files[0];
    if (!uploadedFile) {
      setFile(null);
      setError('Nenhum arquivo selecionado');
      return;
    }

    const type = uploadedFile.type.split('/')[0];
    if (!['image', 'audio', 'application'].includes(type)) {
      setError('Tipo de arquivo não suportado. Por favor, envie uma imagem, áudio ou documento.');
      setFile(null);
      return;
    }

    setFileType(type);
    setFile(uploadedFile);
    setConvertedFile(null);
    setError('');
  };

  const handleConversion = (format) => {
    if (!file) {
      setError('Por favor, envie um arquivo primeiro.');
      return;
    }

    setIsProcessing(true);
    setProgress(20);
    const formData = new FormData();
    formData.append('file', file);
    formData.append('format', format);

    let apiEndpoint = `${apiURL}/convert/image`;
    if (fileType === 'audio') apiEndpoint = `${apiURL}/convert/audio`;
    else if (fileType === 'application' && file.name.endsWith('.pdf')) apiEndpoint = `${apiURL}/convert/pdf_to_images`;
    else if (fileType === 'application' && file.name.endsWith('.docx')) apiEndpoint = `${apiURL}/convert/docx_to_pdf`;

    fetch(apiEndpoint, {
      method: 'POST',
      body: formData,
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao processar o arquivo');
        }
        setProgress(70);
        return response.blob();
      })
      .then((blob) => {
        const downloadUrl = window.URL.createObjectURL(blob);
        setConvertedFile(downloadUrl);
        setProgress(100);
        setIsProcessing(false);
      })
      .catch((err) => {
        setError(err.message || 'Erro inesperado ao converter o arquivo');
        console.error('Erro no processamento do arquivo:', err);
        setProgress(0);
        setIsProcessing(false);
      });
  };

  const reset = () => {
    setFile(null);
    setFileType('');
    setConvertedFile(null);
    setProgress(0);
    setError('');
    setIsProcessing(false);
  };

  return (
    <ThemeProvider>
      <div className="app-container">
        {/* Navbar simples */}
        <nav className="bg-gray-800 p-4 text-white w-full text-center">
          <h1 className="text-xl font-bold"></h1>
        </nav>

        <div className="header-container">
          <h1 className="text-3xl font-bold mb-4">Conversor de Arquivos</h1>
          {error && (
            <p className="text-red-500">
              <strong>Erro:</strong> {error}
            </p>
          )}
        </div>

        {/* File Uploader usando o Input modificado */}
        <div className="upload-container">
          <label className="custom-file-upload">
            <Input type="file" onChange={handleFileUpload} className="mt-4" />
            Escolher arquivo
          </label>
          {/* Exibe o nome do arquivo escolhido ou mensagem padrão */}
          <div id="file-chosen" className="text-purple-400">
            {file ? `Arquivo: ${file.name}` : 'Nenhum arquivo escolhido'}
          </div>
        </div>

        {/* ConverterForm simples com botões */}
        {file && (
          <div className="mt-6">
            <Button onClick={() => handleConversion('jpeg')}>Converter para JPEG</Button>
            <Button onClick={() => handleConversion('png')} className="ml-4">Converter para PNG</Button>
            <Button onClick={() => handleConversion('pdf')} className="ml-4">Converter para PDF</Button>
          </div>
        )}

        {/* Barra de progresso */}
        {progress > 0 && (
          <div className="mt-4">
            <Progress value={progress} />
          </div>
        )}

        {/* Link para download do arquivo convertido */}
        {convertedFile && (
          <div className="mt-6 text-center">
            <a href={convertedFile} download="arquivo_convertido" className="text-blue-500">
              Baixar Arquivo Convertido
            </a>
          </div>
        )}

        {/* Footer simples */}
        <footer className="bg-gray-800 text-white text-center p-4 mt-10">
          <p>© 2024 Meu Conversor de Arquivos</p>
        </footer>
      </div>
    </ThemeProvider>
  );
};

export default App;
