import React, { useState, useEffect } from 'react';

const ConverterForm = ({ onSubmit, formats }) => {
  const [selectedFormat, setSelectedFormat] = useState(formats[0]);
  const [isButtonDisabled, setIsButtonDisabled] = useState(false);

  useEffect(() => {
    // Habilita o botão se houver um formato selecionado
    setIsButtonDisabled(!selectedFormat);
  }, [selectedFormat]);

  const handleFormatChange = (e) => {
    setSelectedFormat(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault(); // Evita o refresh da página ao submeter o formulário
    if (selectedFormat) {
      onSubmit(selectedFormat);
    }
  };

  return (
    <div className="converter-form">
      <label htmlFor="format-select">Escolha o formato de conversão:</label>
      <select id="format-select" onChange={handleFormatChange} value={selectedFormat}>
        {formats.map((format) => (
          <option key={format} value={format}>
            {format.toUpperCase()}
          </option>
        ))}
      </select>

      {/* Botão de conversão */}
      <button 
        onClick={handleSubmit} 
        disabled={isButtonDisabled} 
        className={isButtonDisabled ? 'disabled' : ''}
      >
        Converter
      </button>
    </div>
  );
};

export default ConverterForm;
