import React, { useState } from 'react';
import Navbar from './components/Navbar/navbar';
import Footer from './components/Footer/footer';
import FileUploader from './components/FileUploader';
import ConverterForm from './components/ConverterForm';
import ProgressBar from './components/ProgressBar';
import { ThemeProvider, useTheme } from './context/ThemeContext';
import './index.css'; // Estilos atualizados

const App = () => {
  const [file, setFile] = useState(null);
  const [fileType, setFileType] = useState('');
  const [convertedFile, setConvertedFile] = useState(null);
  const [progress, setProgress] = useState(0);
  const [error, setError] = useState('');
  const [isProcessing, setIsProcessing] = useState(false);
  const { isDarkMode } = useTheme();
  const apiURL = process.env.REACT_APP_API_URL;

  // Função para lidar com o upload do arquivo
  const handleFileUpload = (uploadedFile) => {
    const type = uploadedFile.type.split('/')[0];
    if (!['image', 'audio', 'application'].includes(type)) {
      setError('Tipo de arquivo não suportado. Por favor, envie uma imagem, áudio ou documento.');
      setFile(null);
      return;
    }

    setFileType(type);
    setFile(uploadedFile);
    setConvertedFile(null);
    setError('');
  };

  // Função para lidar com a conversão de arquivos
  const handleConversion = (format) => {
    if (!file) {
      setError('Por favor, envie um arquivo primeiro.');
      return;
    }

    setIsProcessing(true);
    setProgress(20);
    const formData = new FormData();
    formData.append('file', file);
    formData.append('format', format);

    let apiEndpoint = `${apiURL}/convert/image`;
    if (fileType === 'audio') apiEndpoint = `${apiURL}/convert/audio`;
    else if (fileType === 'application' && file.name.endsWith('.pdf')) apiEndpoint = `${apiURL}/convert/pdf_to_images`;
    else if (fileType === 'application' && file.name.endsWith('.docx')) apiEndpoint = `${apiURL}/convert/docx_to_pdf`;

    fetch(apiEndpoint, {
      method: 'POST',
      body: formData,
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Erro ao processar o arquivo');
        }
        setProgress(70);
        return response.blob();
      })
      .then((blob) => {
        const downloadUrl = window.URL.createObjectURL(blob);
        setConvertedFile(downloadUrl);
        setProgress(100);
        setIsProcessing(false);
      })
      .catch((err) => {
        setError(err.message || 'Erro inesperado ao converter o arquivo');
        setProgress(0);
        setIsProcessing(false);
      });
  };

  // Função para resetar o estado do upload/conversão
  const reset = () => {
    setFile(null);
    setFileType('');
    setConvertedFile(null);
    setProgress(0);
    setError('');
    setIsProcessing(false);
  };

  return (
    <ThemeProvider>
      <div className={`min-h-screen flex flex-col items-center justify-center ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'}`}>
        <Navbar />

        <div className="text-center mt-10">
          <h1 className="text-5xl font-extrabold mb-8">PyScriptConvert</h1>

          {error && (
            <p className="text-red-500 text-lg mb-6">
              <strong>Erro:</strong> {error}
            </p>
          )}
        </div>

        {/* Componente de upload de arquivos */}
        <div className="w-full max-w-md mx-auto">
          <FileUploader onFileUpload={handleFileUpload} reset={reset} />
        </div>

        {/* Pré-visualização de imagem */}
        {file && fileType === 'image' && (
          <div className="mt-8">
            <img 
              src={URL.createObjectURL(file)} 
              alt="Pré-visualização" 
              className="max-w-md mx-auto rounded-lg shadow-lg border border-gray-300"
            />
          </div>
        )}

        {/* Exibição do formulário de conversão */}
        {file && (
          <div className="w-full max-w-md mx-auto mt-6">
            <ConverterForm
              onSubmit={handleConversion}
              formats={
                fileType === 'image'
                  ? ['jpeg', 'png', 'gif', 'svg']
                  : fileType === 'audio'
                  ? ['mp3', 'wav', 'ogg', 'flac', 'aac']
                  : file.name.endsWith('.pdf')
                  ? ['png', 'jpeg', 'tiff', 'bmp']
                  : ['pdf']
              }
              isProcessing={isProcessing}
            />
          </div>
        )}

        {/* Barra de progresso com animação */}
        {progress > 0 && (
          <div className="w-full max-w-md mx-auto mt-6">
            <ProgressBar progress={progress} />
          </div>
        )}

        {/* Link para baixar o arquivo convertido */}
        {convertedFile && (
          <div className="mt-8">
            <a href={convertedFile} download="arquivo_convertido" className="bg-blue-500 text-white py-2 px-6 rounded-lg shadow-lg hover:bg-blue-600 transition-all">
              Baixar Arquivo Convertido
            </a>
          </div>
        )}

        <Footer />
      </div>
    </ThemeProvider>
  );
};

export default App;
