import React, { useState } from 'react';

const Input = ({ type = 'text', className = '', ...props }) => {
  const [chosenFileName, setChosenFileName] = useState('Nenhum arquivo escolhido');

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setChosenFileName(file.name); // Atualiza o estado com o nome do arquivo
    } else {
      setChosenFileName('Nenhum arquivo escolhido');
    }

    if (props.onChange) {
      props.onChange(event); // Chama o onChange do componente pai, se presente
    }
  };

  if (type === 'file') {
    return (
      <div className="file-upload-wrapper">
        <label className="custom-file-upload">
          Escolher arquivo
          <input 
            type="file" 
            className={`hidden ${className}`} 
            onChange={handleFileChange} 
            {...props} 
          />
        </label>
        {/* Atualiza o texto para mostrar o nome do arquivo escolhido */}
        <span id="file-chosen">{chosenFileName}</span>
      </div>
    );
  }

  return (
    <input
      type={type}
      className={`px-4 py-2 border rounded ${className}`}
      {...props}
    />
  );
};

export default Input;
