// src/components/ui/input.js
import React from 'react';

const Input = ({ type = 'text', className = '', ...props }) => {
  // Verifica se o input é do tipo "file" para aplicar um estilo especial
  if (type === 'file') {
    return (
      <div className="file-upload-wrapper">
        <label className="custom-file-upload">
          Escolher arquivo
          <input type="file" className={`hidden ${className}`} {...props} />
        </label>
        <span id="file-chosen">Nenhum arquivo escolhido</span>
      </div>
    );
  }

  // Input padrão para outros tipos
  return (
    <input
      type={type}
      className={`px-4 py-2 border rounded ${className}`}
      {...props}
    />
  );
};

export default Input;
