import React, { useState } from 'react';
import { Button, ThemeProvider } from '@material-ui/core';
import { createTheme } from '@material-ui/core/styles';
import './App.css';

const App = () => {
  const [file, setFile] = useState(null);
  const [fileType, setFileType] = useState('');
  const [outputFormats, setOutputFormats] = useState([]);
  const [progress, setProgress] = useState(0);

  const handleFileChange = (event) => {
    const selectedFile = event.target.files[0];
    setFile(selectedFile);
    setFileType(selectedFile.type);
  };

  const handleFormatChange = (event) => {
    const { value } = event.target;
    setOutputFormats((prev) =>
      prev.includes(value)
        ? prev.filter((format) => format !== value)
        : [...prev, value]
    );
  };

  const handleConvert = async () => {
    // Simulação de progresso
    for (let i = 0; i <= 100; i += 20) {
      setProgress(i);
      await new Promise((resolve) => setTimeout(resolve, 100)); // Simula tempo de processamento
    }
    setProgress(100);
    // Aqui você pode adicionar a lógica para fazer o download do arquivo convertido
  };

  const theme = createTheme({
    palette: {
      primary: {
        main: '#1976d2',
      },
      secondary: {
        main: '#dc004e',
      },
    },
  });

  return (
    <ThemeProvider theme={theme}>
      <div className="App">
        <h1 className="text-white">Conversor de Arquivos</h1>
        <input type="file" onChange={handleFileChange} />
        {file && (
          <div>
            <p className="text-white mt-2">Você está convertendo: {file.name}</p>
          </div>
        )}
        {file && fileType === 'audio' && (
          <p className="text-white mt-2">Você pode converter áudio para MP3, WAV, OGG, FLAC, ou M4A.</p>
        )}
        {file && fileType === 'application' && file.name.endsWith('.pdf') && (
          <p className="text-white mt-2">Você pode converter PDF para JPG ou PNG.</p>
        )}
        {outputFormats.length > 0 && (
          <div>
            <h2 className="text-white">Formatos selecionados:</h2>
            <ul>
              {outputFormats.map((format) => (
                <li key={format} className="text-white">{format}</li>
              ))}
            </ul>
          </div>
        )}
        <Button variant="contained" color="primary" onClick={handleConvert}>
          Converter
        </Button>
        {progress > 0 && progress < 100 && (
          <p className="text-white mt-2">Progresso: {progress}%</p>
        )}
        {progress === 100 && (
          <p className="text-white mt-2">Conversão concluída!</p>
        )}
        <div className="mt-4">
          <Button variant="contained" color="secondary" onClick={() => setFile(null)}>
            Resetar
          </Button>
        </div>
      </div>
    </ThemeProvider>
  );
};

export default App;
