import React, { useState, useEffect } from 'react';
import './ConverterForm.css'; // Adicionei o CSS específico

const ConverterForm = ({ onSubmit, formats, isProcessing }) => {
  const [selectedFormat, setSelectedFormat] = useState(formats[0]);
  const [isButtonDisabled, setIsButtonDisabled] = useState(false);

  useEffect(() => {
    // Desabilita o botão se não houver formato selecionado ou se o processamento estiver ativo
    setIsButtonDisabled(!selectedFormat || isProcessing);
  }, [selectedFormat, isProcessing]);

  const handleFormatChange = (e) => {
    setSelectedFormat(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault(); // Evita o refresh da página ao submeter o formulário
    if (selectedFormat && !isProcessing) {
      onSubmit(selectedFormat);
    }
  };

  return (
    <div className="converter-form">
      <label htmlFor="format-select">Escolha o formato de conversão:</label>

      {/* Dropdown de seleção de formatos */}
      <select 
        id="format-select" 
        onChange={handleFormatChange} 
        value={selectedFormat} 
        disabled={isProcessing}  // Desabilita enquanto estiver processando
      >
        {formats.map((format) => (
          <option key={format} value={format}>
            {format.toUpperCase()}
          </option>
        ))}
      </select>

      {/* Botão de conversão */}
      <button 
        onClick={handleSubmit} 
        disabled={isButtonDisabled} 
        className={isButtonDisabled ? 'disabled' : ''}
      >
        {isProcessing ? 'Convertendo...' : 'Converter'}
      </button>
    </div>
  );
};

export default ConverterForm;
