// src/components/ui/progress.js
import React from 'react';

const Progress = ({ value }) => {
  return (
    <div className="w-full bg-gray-700 rounded shadow-md max-w-lg mx-auto">
      <div
        className="bg-purple-500 text-xs font-medium text-purple-100 text-center p-1 leading-none rounded transition-all duration-300"
        style={{ width: `${value}%`, boxShadow: '0 0 8px #a855f7, 0 0 15px #c77dff' }}
      >
        {value}%
      </div>
    </div>
  );
};

export default Progress;
