import React, { useState, useEffect } from "react";
import Button from "./components/ui/button";
import Input from "./components/ui/input";
import Progress from "./components/ui/progress";
import { ThemeProvider } from "./context/ThemeContext"; // Contexto de tema
import "./App.css";

const App = () => {
  const [file, setFile] = useState(null);
  const [fileType, setFileType] = useState("");
  const [convertedFile, setConvertedFile] = useState(null);
  const [progress, setProgress] = useState(0);
  const [error, setError] = useState("");
  const [isProcessing, setIsProcessing] = useState(false);
  const [downloadFormat, setDownloadFormat] = useState(""); // Armazena o formato do download

  const apiURL = process.env.REACT_APP_API_URL || "http://localhost:5000";

  const handleFileUpload = (event) => {
    const uploadedFile = event.target.files[0];

    if (!uploadedFile) {
      setError("Nenhum arquivo selecionado.");
      setFile(null);
      return;
    }

    const type = uploadedFile.type.split("/")[0];
    const extension = uploadedFile.name.split(".").pop().toLowerCase();
    const supportedFormats = {
      image: ["jpeg", "jpg", "png", "svg", "bmp", "tiff"],
      audio: ["mp3", "wav", "ogg", "flac", "m4a"],
      application: ["pdf", "docx"],
    };

    if (!supportedFormats[type]?.includes(extension)) {
      setError(`Formato de arquivo .${extension} não é suportado.`);
      setFile(null);
      return;
    }

    setFileType(type);
    setFile(uploadedFile);
    setConvertedFile(null);
    setError("");
  };

  const simulateProgress = (target) => {
    let currentProgress = progress;
    const interval = setInterval(() => {
      currentProgress += 5;
      if (currentProgress >= target) {
        clearInterval(interval);
        setProgress(target);
      } else {
        setProgress(currentProgress);
      }
    }, 100);
  };

  const handleConversion = (format) => {
    if (!file) {
      setError("Por favor, envie um arquivo primeiro.");
      return;
    }

    setIsProcessing(true);
    setProgress(0);
    setDownloadFormat(format);

    const formData = new FormData();
    formData.append("file", file);
    formData.append("format", format);

    let apiEndpoint = `${apiURL}/convert/image`;
    if (fileType === "audio") {
      apiEndpoint = `${apiURL}/convert/audio`;
    } else if (fileType === "application" && file.name.endsWith(".pdf")) {
      apiEndpoint = `${apiURL}/convert/pdf_to_images`;
    } else if (fileType === "application" && file.name.endsWith(".docx")) {
      apiEndpoint = `${apiURL}/convert/docx_to_pdf`;
    }

    simulateProgress(50);

    fetch(apiEndpoint, {
      method: "POST",
      body: formData,
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(
            `Erro do servidor: ${response.status} - ${response.statusText}`
          );
        }
        simulateProgress(70);
        return response.blob();
      })
      .then((blob) => {
        const downloadUrl = window.URL.createObjectURL(blob);
        setConvertedFile(downloadUrl);
        simulateProgress(100);
        setIsProcessing(false);
      })
      .catch((err) => {
        setError(err.message || "Erro inesperado ao converter o arquivo");
        setProgress(0);
        setIsProcessing(false);
      });
  };

  const reset = () => {
    if (convertedFile) {
      window.URL.revokeObjectURL(convertedFile);
    }
    setFile(null);
    setFileType("");
    setConvertedFile(null);
    setProgress(0);
    setError("");
    setIsProcessing(false);
    setDownloadFormat("");
  };

  useEffect(() => {
    const handleOnline = () => setError("");
    const handleOffline = () => setError("Sem conexão com a internet.");
    window.addEventListener("online", handleOnline);
    window.addEventListener("offline", handleOffline);

    return () => {
      window.removeEventListener("online", handleOnline);
      window.removeEventListener("offline", handleOffline);
    };
  }, []);

  return (
    <ThemeProvider>
      <div className="app-container">
        <div className="header-container">
          <h1 className="text-3xl font-bold mb-4">Conversor de Arquivos</h1>
          {error && (
            <p className="text-red-500">
              <strong>Erro:</strong> {error}
            </p>
          )}
        </div>

        <div className="upload-container">
          <Input type="file" onChange={handleFileUpload} className="mt-4" />

          <div className="file-info mt-4">
            <p className="text-white">
              <strong>Tipos de arquivos suportados:</strong>
            </p>
            <ul className="text-white">
              <li>Imagens: JPG, PNG, SVG, BMP, TIFF</li>
              <li>Áudio: MP3, WAV, OGG, FLAC, M4A</li>
              <li>Documentos: PDF, DOCX</li>
            </ul>
          </div>

          {file && fileType === "image" && (
            <div className="mt-4">
              <img
                src={URL.createObjectURL(file)}
                alt="Pré-visualização"
                className="image-preview"
              />
              <p className="text-white mt-2">
                Você pode converter imagens para JPEG, PNG, SVG, BMP, ou TIFF.
              </p>
            </div>
          )}

          {file && fileType === "audio" && (
            <p className="text-white mt-2">
              Você pode converter áudio para MP3, WAV, OGG, FLAC ou M4A.
            </p>
          )}

          {file && fileType === "application" && file.name.endsWith(".pdf") && (
            <p className="text-white mt-2">
              Você pode converter PDFs para imagens.
            </p>
          )}

          {file &&
            fileType === "application" &&
            file.name.endsWith(".docx") && (
              <p className="text-white mt-2">
                Você pode converter DOCX para PDF.
              </p>
            )}
        </div>

        {file && (
          <div className="mt-6">
            {fileType === "image" && (
              <>
                <Button onClick={() => handleConversion("jpeg")}>
                  Converter para JPEG
                </Button>
                <Button
                  onClick={() => handleConversion("png")}
                  className="ml-4"
                >
                  Converter para PNG
                </Button>
              </>
            )}
          </div>
        )}

        {progress > 0 && (
          <div className="mt-4">
            <Progress value={progress} />
          </div>
        )}

        {convertedFile && (
          <div className="mt-6 text-center">
            <a
              href={convertedFile}
              download={`${file.name
                .split(".")
                .slice(0, -1)
                .join(".")}.${downloadFormat}`}
              className="download-link"
            >
              Baixar Arquivo Convertido
            </a>
          </div>
        )}

        <div className="mt-6">
          <Button onClick={reset} className="bg-red-500">
            Resetar
          </Button>
        </div>
      </div>
    </ThemeProvider>
  );
};

export default App;
