// src/components/ui/button.js
import React from 'react';

const Button = ({ children, variant = 'default', ...props }) => {
  // Estilos base que incluem padding, cor de texto, borda arredondada
  const baseStyles = 'px-4 py-2 font-bold text-white rounded focus:outline-none transition-all duration-300';

  // Variantes que determinam o estilo baseado no "variant" (opções: default, secondary, etc.)
  const variants = {
    default: 'bg-purple-500 hover:bg-purple-700 shadow-neon-purple',
    secondary: 'bg-gray-500 hover:bg-gray-700 shadow-neon-gray',
  };

  // Classe combinada
  const className = `${baseStyles} ${variants[variant]}`;

  return (
    <button className={className} {...props}>
      {children}
    </button>
  );
};

export default Button;
