import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';
import './FileUploader.css';

const FileUploader = ({ onFileUpload, reset }) => {
  const [fileName, setFileName] = useState('');

  // Configurando o react-dropzone
  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop: (acceptedFiles) => {
      const file = acceptedFiles[0];
      if (file) {
        setFileName(file.name);
        onFileUpload(file); // Chama a função para processar o upload
      }
    },
    accept: 'image/*,audio/*,.pdf,.docx', // Tipos de arquivo aceitos
  });

  const handleReset = () => {
    reset(); // Chama a função de reset do componente pai
    setFileName(''); // Limpa o nome do arquivo
  };

  return (
    <div className="file-uploader-container">
      {/* Área de arrastar e soltar com feedback visual */}
      <div
        {...getRootProps()}
        className={`file-uploader ${isDragActive ? 'active' : ''}`}
      >
        <input {...getInputProps()} />
        <p>
          {isDragActive
            ? 'Solte o arquivo aqui...'
            : fileName
            ? `Arquivo: ${fileName}`
            : 'Clique ou arraste o arquivo aqui'}
        </p>
      </div>

      {/* Botão Reset */}
      <button 
        onClick={handleReset} 
        disabled={!fileName} 
        className={`reset-button ${!fileName ? 'disabled' : ''}`}
      >
        Resetar
      </button>

      {/* Exibir nome do arquivo selecionado */}
      {fileName && <p>Arquivo selecionado: {fileName}</p>}
    </div>
  );
};

export default FileUploader;
