// src/components/ui/Button.js
import React from 'react';
import { cn } from '@/lib/utils'; // Se você já tiver essa função utilitária para classes

export const Button = ({ children, variant = 'default', ...props }) => {
  const baseStyles = 'px-4 py-2 font-bold text-white rounded';
  const variants = {
    default: 'bg-blue-500 hover:bg-blue-700',
    secondary: 'bg-gray-500 hover:bg-gray-700',
  };

  const className = cn(baseStyles, variants[variant]);

  return (
    <button className={className} {...props}>
      {children}
    </button>
  );
};

export default Button;
